// get-properties
//
// Gets all properties to update
//
/* global module */
var getNextVersion    = require('./get-next-version'),
    getOktaProperties = require('./get-okta-properties'),
    logger            = require('winston');

function getProperties(pkgName, pkgVersion, branch) {
  var props = {};

  logger.log('info', 'Determining next version');
  props.version = getNextVersion(pkgName, pkgVersion, branch);

  logger.log('info', 'Getting okta properties');
  props.okta = getOktaProperties(props.version);

  return props;
}

module.exports = getProperties;
