define([
  'shared/models/Model',
  'shared/models/BaseCollection'
],
function (Model, BaseCollection) {

  var App = Model.extend({
    props: {
      appId: 'string',
      name: 'string',
      displayName: 'string',
      logoImage: 'string',
      description: 'string',
      appStatus: 'string',
      verificationStatus: 'string',
      instanceCount: 'number',
      documentationCount: 'number',
      evaluationStatus: 'string',
      'appDoc.documentationId': 'string',
      'appDoc.appId': 'string',
      'appDoc.signOnMode': 'string',
      'appDoc.customerType': 'string',
      'appDoc.mustContactAppVendor': 'boolean',
      'appDoc.appSettingsPages': 'string',
      'appDoc.docLinks': 'string',
      'appDoc.configurationTips': 'string',
      'appDoc.completeIntegration': 'boolean',
      'appDoc.enableSaml': 'string',
      'appDoc.devAccount': 'string',
      'appDoc.contactName': 'string',
      'appDoc.contactTitle': 'string',
      'appDoc.contactEmail': 'string'
    },

    local: {
      '__expand__': 'boolean'
    },

    idAttribute: 'appId'
  });

  var Apps = BaseCollection.extend({
    model: App,
    url: '/api/internal/apps/appAnalyst',
    parse: function (response) {
      this.setPagination(response.nextAppsPageURL);
      return response.apps;
    }
  });

  return {
    Model:  App,
    Collection: Apps
  };
});
