define([
  'shared/models/BaseCollection',
  'shared/models/BaseModel'
],
function (BaseCollection, BaseModel) {

  var AppLink = BaseModel.extend({
    defaults: function () {
      return {
        name: '',
        title: '',
        destination: '/',
        image: null
      };
    }
  });

  var AppLinks = BaseCollection.extend({
    model: AppLink
  });

  return {
    Collection: AppLinks,
    Model: AppLink
  };
});