define([
  'shared/models/BaseCollection', 'shared/models/BaseModel'
], function (BaseCollection, BaseModel) {
  var AppUserProperty = BaseModel.extend({
    defaults: {
      internalName: null,
      name: null,
      label: null,
      type: 'STRING',
      helpText: null,
      hidden: false,
      required: false,
      pattern: null,
      updatable: true,
      displayOrder: 0,
      referenceTypeName: null,
      enumTypeName: null,
      local: false,
      personal: false
    }
  });

  var AppUserProperties = BaseCollection.extend({
    model: AppUserProperty
  });

  return {
    Model: AppUserProperty,
    Collection: AppUserProperties
  };
});