define([
  'shared/models/BaseCollection', 'shared/models/BaseModel'
], function (BaseCollection, BaseModel) {

  var CustomProperty = BaseModel.extend({
    defaults: {
      internalName: null,
      name: null,
      label: null,
      type: 'STRING',
      helpText: null,
      hidden: false,
      required: false,
      pattern: null,
      updatable: true,
      displayOrder: 0,
      referenceTypeName: null,
      enumTypeName: null
    }
  });

  var CustomProperties = BaseCollection.extend({
    model: CustomProperty
  });

  return {
    Model: CustomProperty,
    Collection: CustomProperties
  };
});