define([
  'shared/models/BaseCollection', 'shared/models/BaseModel', 'shared/models/EnumValue'
], function (BaseCollection, BaseModel, EnumValue) {

  var EnumType = BaseModel.extend({
    defaults: function () {
      return {
        internalName: null,
        name: null,
        values: new EnumValue.Collection()
      };
    },

    parse: function (resp) {
      resp.values = new EnumValue.Collection(resp.values);
      return resp;
    }
  });

  var EnumTypes = BaseCollection.extend({
    model: function (attrs, options) {
      options.parse = true;
      return new EnumType(attrs, options);
    }
  });

  return {
    Model: EnumType,
    Collection: EnumTypes
  };
});