define([
  './UserApp'
], function (UserApp) {

  /**
   * @class GroupApp.Model
   * @extends {UserApp.Model}
   * An app instance assigned/unassigned to a group
   */
  var GroupAppInstance = UserApp.Model;

  /**
   * @class GroupApp.Collection
   * @extends {UserApp.Collection}
   * App instances assigned/unassigned to a group
   */
  var GroupAppInstances = UserApp.Collection.extend({

    model: GroupAppInstance,

    appOrigin: 'group'

  });

  return {
    Model: GroupAppInstance,
    Collection: GroupAppInstances
  };
});