define([
  'shared/models/Model',
  'shared/models/BaseCollection'
],
function (Model, BaseCollection) {
  var MobileApp = Model.extend({
    props: {
      'name': 'string',
      'displayName': 'string',
      'description': 'string',
      'nativeId': 'string',
      'callbackUri': 'string',
      'platform': ['string', true, 'ANDROID'],
      'deviceSupport': ['string', true, 'ANDROID_UNIVERSAL'],
      'oktaMobileOpenUri': 'string',
      'oktaMobileCallbackUri': 'string',
      'omcEnabled': 'boolean',
      'linkId': 'string'
    },

    constructor: function () {
      Model.prototype.constructor.apply(this, arguments);
    }
  });

  var MobileApps = BaseCollection.extend({
    model: MobileApp
  });

  return {
    Model: MobileApp,
    Collection: MobileApps
  };
});
