define(['shared/models/BaseCollection', 'shared/models/BaseModel'],
function (BaseCollection, BaseModel) {

  var Org = BaseModel.extend({
    defaults: {
      id: undefined,
      name: undefined,
      subdomain: undefined,
      status: undefined,
      createdDate: undefined,
      expirationDate: undefined,
      edition: undefined
    }
  });

  var Orgs = BaseCollection.extend({
    model: Org,
    url: '/api/internal/su/orgs',
    parse: function (payload) {
      this.setPagination(payload.nextOffset > 0 && {offset: payload.nextOffset}, {fromFetch: true});
      return payload.orgs;
    }
  });

  return {
    Model: Org,
    Collection: Orgs
  };

});

