define(['underscore', 'shared/models/BaseModel'], function (_, BaseModel) {

  var SamlDoc = BaseModel.extend({
    defaults: function () {
      return {
        customerType: undefined,
        internalApp: false,
        contactVendor: false,
        appSettingsPages: '',
        docLinks: '',
        configurationTips: '',
        completeIntegration: undefined,
        enableSaml: undefined,
        devAccount: undefined,
        contactName: undefined,
        contactTitle: undefined,
        contactEmail: undefined
      };
    },

    idAttribute: '__appDocId__',

    urlRoot: '/api/internal/orgadmin/apps/saml/doc',

    toJSON: function () {
      var json = BaseModel.prototype.toJSON.apply(this, arguments);
      if (json.customerType === 'CUSTOMER') {
        return _.omit(json, 'completeIntegration', 'enableSaml', 'devAccount',
          'contactName', 'contactTitle', 'contactEmail');
      } else if (json.customerType === 'PARTNER') {
        return _.omit(json, 'internalApp', 'contactVendor',
          'appSettingsPages', 'docLinks', 'configurationTips');
      }
      return json;
    }
  });

  return SamlDoc;
});
