define([
  'shared/models/Model',
  'shared/util/TemplateUtil'
], function (Model, TemplateUtil) {

  var urlTemplate = TemplateUtil.tpl('/api/v1/internal/apps/{{instanceId}}/userAssignmentHelpText');

  return Model.extend({
    props: {
      instanceId: 'string',
      appName: 'string',
      userAssignmentHelpTextSupported: 'boolean',
      displayUserAssignmentHelpText: 'boolean'
    },

    initialize: function (options) {
      options || (options = {});
      if (!options['instanceId']) {
        throw 'Cannot initialize UserAssignmentHelpText without an instance id';
      }
      this.instanceId = options.instanceId;
    },

    url: function () {
      return urlTemplate({instanceId: this.instanceId});
    }
  });
});
