define([
  'shared/util/TemplateUtil',
  'shared/models/BaseSchema'
], function (TemplateUtil, BaseSchema) {

  var UserSchema = BaseSchema.Model.extend({
    urlRoot: '/api/v1/user/schemas'
  });

  var tmpl = TemplateUtil.tpl('/api/v1/user/types/{{objTypeId}}/schemas');

  var UserSchemas = BaseSchema.Collection.extend({
    model: UserSchema,
    initialize: function (models, options) {
      this.objTypeId = options && options.objTypeId;
    },
    url: function () {
      return tmpl(this);
    }
  });

  return {
    Model: UserSchema,
    Collection: UserSchemas
  };

});
