define({
  PUSH: 'PUSH',
  DONT_PUSH: 'DONT_PUSH',

  getIconClass: function (pushStatus) {
    var defaultClass = 'mapping-rule-icon',
        additionalClass = '';
    switch (pushStatus) {
    case this.PUSH:
      additionalClass = 'mapping-rule-create-update';
      break;
    case this.DONT_PUSH:
      additionalClass = 'mapping-rule-create-only';
      break;
    default:
      additionalClass = 'mapping-rule-not-mapped';
    }
    return defaultClass + ' ' + additionalClass;
  },

  getDropDownWrapperClass: function (pushStatus) {
    switch (pushStatus) {
    case this.PUSH:
      return 'mapping-rule-create-update-dropdown';
    case this.DONT_PUSH:
      return 'mapping-rule-create-only-dropdown';
    default:
      return 'mapping-rule-not-mapped-dropdown';
    }
  }
});