define([
  'shared/util/TemplateUtil'
], function (TemplateUtil) {

  return {
    OKTA_DEFAULT_LOGO_RESOURCE: '/img/logos/okta-logo.png',
    APP_DEFAULT_LOGO_RESOURCE: '/img/logos/default.png',
    DOCUMENTATION: 'https://support.okta.com/articles/Knowledge_Article/About-Universal-Directory',
    INTRO_VIDEO: 'http://youtu.be/PBCV2PxIvnc',

    getAppConfigResource: function (appName, appInstanceId, tabName) {
      if (appName && appInstanceId) {
        return TemplateUtil.tpl('/admin/app/{{name}}/instance/{{id}}/{{#if tab}}#{{tab}}{{/if}}')({
          name: appName,
          id: appInstanceId,
          tab: tabName
        });
      } else {
        return '';
      }
    }
  };
});
