define([
  'underscore',
  'shared/util/StringUtil'
], function (_, StringUtil) {

  var STATUS_LABEL_KEYS = {
    'STAGED'           : 'appinstance.userassignment.user.status.staged',
    'ACTIVE'           : 'appinstance.userassignment.user.status.active',
    'RECOVERY'         : 'appinstance.userassignment.user.status.recovery',
    'PROVISIONED'      : 'appinstance.userassignment.user.status.provisioned',
    'DEPROVISIONED'    : 'appinstance.userassignment.user.status.deprovisioned',
    'LOCKED_OUT'       : 'appinstance.userassignment.user.status.lockedout',
    'PASSWORD_EXPIRED' : 'appinstance.userassignment.user.status.passwordexpired',
    'SUSPENDED'        : 'appinstance.userassignment.user.status.suspended'
  };

  return {

    getLabel: function (userStatus, bundle) {

      if (!_.isString(userStatus)) {
        return '';
      }

      var labelKey = STATUS_LABEL_KEYS[userStatus.toUpperCase()];

      return labelKey ? StringUtil.localize(labelKey, bundle || 'messages') : '';
    }
  };
});
