define(['shared/views/BaseView'], function (BaseView) {

  return BaseView.extend({

    className: 'app-logo-wrap clearfix',

    template: '\
      <div class="app-logo">\
        <div class="app-logo-bg rounded-4">\
          <img src="{{image}}" class="{{name}}" alt="{{alt}}">\
        </div>\
      </div>\
    ',

    updateImage: function (url) {
      this.$('img').attr('src', url);

      return this;
    }
  });

});
