define(['shared/views/BaseView', './ElExplainModal'], function (BaseView, ElExplainModal) {

  var template = '<a href="#">Learn more</a> about the expressions you can use in the custom rule';

  return BaseView.extend({

    tagName: 'p',

    className: 'o-form-explain',

    events: {
      'click a': function (e) {
        e.preventDefault();
        this.modal = new ElExplainModal();
        this.modal.render();
      }
    },

    render: function () {
      this.$el.html(template);
      return this;
    }
  });
});