define(['shared/views/components/BaseModalDialog'], function (BaseModalDialog) {

  return BaseModalDialog.extend({

    template: '\
      <div class="simplemodal-data saml-attributes-learn-more-modal clearfix">\
        <h2 class="block">\
          Custom Field Mapping Expressions\
        </h2>\
        <div class="padding-20">\
          <h3>Overview</h3>\
          <p>You can use a combination of user properties and functions to create a custom expression.</p>\
          <p>Use the following syntax to reference an expression: <kbd>&#36;{expression}</kbd></p>\
          <h3>Properties</h3>\
          <p>Properties refer to a user\'s Okta user profile fields. Examples include:</p>\
          <ul>\
            <li><kbd class="text-dark-green">user.login</kbd></li>\
            <li><kbd class="text-dark-green">user.email</kbd></li>\
            <li><kbd class="text-dark-green">user.firstName</kbd></li>\
            <li><kbd class="text-dark-green">user.lastName</kbd></li>\
          </ul>\
          <h3>Functions</h3>\
          <p>Use the following functions to apply basic transformations to the properties listed above:</p>\
          <ul>\
            <li><kbd>f:toUpperCase(<span class="text-dark-green">property</span>)</kbd></li>\
            <li><kbd>f:toLowerCase(<span class="text-dark-green">property</span>)</kbd></li>\
            <li><kbd>f:substringBefore(<span class="text-dark-green">property</span>, <span class="text-orange">searchString</span>)</kbd></li>\
            <li><kbd>f:substringAfter(<span class="text-dark-green">property</span>, <span class="text-orange">searchString</span>)</kbd></li>\
            <li><kbd>f:replace(<span class="text-dark-green">property</span>, <span class="text-orange">oldValue</span>, <span class="text-orange">newValue</span>)</kbd></li>\
          </ul>\
          <h3>Examples</h3>\
          <p>These examples demonstrate combining properties and functions to create custom expressions.</p>\
          <ul class="examples">\
            <li>\
              <p>Use the Okta login:</p>\
              <kbd class="kbd-box">&#36;{<span class="text-dark-green">user.login</span>}</kbd>\
            </li>\
            <li>\
              <p>Use the email address and convert it to lowercase:</p>\
              <kbd class="kbd-box">&#36;{f:toLowerCase(<span class="text-dark-green">user.email</span>)}</kbd>\
             </li>\
            <li>\
              <p>Use the first and last name, joined with a period:</p>\
              <kbd class="kbd-box">&#36;{<span class="text-dark-green">user.firstName</span>}.&#36;{<span class="text-dark-green">user.lastName</span>}</kbd>\
            </li>\
            <li>\
              <p>Use the part of the email address that comes before the "@" symbol:</p>\
              <kbd class="kbd-box">&#36;{f:substringBefore(<span class="text-dark-green">user.email</span>, <span class="text-orange">"@"</span>)}</kbd>\
            </li>\
          </ul>\
        </div>\
      </div>\
      <style type="text/css">\
        #simplemodal-container {height:849px !important;}\
        #simplemodal-container .simplemodal-wrap {overflow:hidden !important;}\
      </style>\
      ',
    params: {
      minWidth: 500,
      fixed: false,
      autoResize: true
    }
  });
});