define([
  'underscore',
  'shared/views/BaseView'
], function (_, BaseView) {

  var options = {
    level: 'success',
    title: 'Great Success!',
    subtitle: 'This action was successful.'
  };

  return BaseView.extend({
    className: 'infobox margin-btm-20',
    tag: 'div',
    template: '\
      <span class="icon {{level}}-16"></span>\
      <h3>{{title}}</h3>\
      <p>{{subtitle}}</p>\
    ',

    initialize: function () {
      _.defaults(this.options, options);
      this.$el.addClass('infobox-' + this.options.level);
    }
  });
});
