define(['shared/views/BaseView'],
  function (BaseView) {

    var View = BaseView.extend({
      tagName: 'li',

      template: '<span class="progress-bar-step-num">{{step}}</span>{{title}}',

      modelEvents: {
        'change:__step__': 'updateStep'
      },

      updateStep: function () {
        if (this.model.get('__step__') === this.options.step) {
          this.$el.addClass('progress-bar-step-active');
        } else {
          this.$el.removeClass('progress-bar-step-active');
        }
      }
    });

    return View;
  });
