define([
  'shared/views/BaseView',
  'shared/util/StringUtil'
], function (BaseView, StringUtil) {
  return BaseView.extend({
    className: 'prompt',

    events: {
      'click input.save': function (e) {
        e.preventDefault();
        this.state.trigger('apply:mapping');
      }
    },

    template: '\
      <h3 class="text-dark-green">{{i18n code="universal-directory.mappings.save.success.prompt.title"}}</h3>\
      <p>{{subtitle}}</p>\
      <input type="button" class="button button-primary save"\
        value="{{i18n code="universal-directory.mappings.apply"}}">\
      <input type="button" class="button cancel" value="{{i18n code="universal-directory.mappings.apply.cancel"}}">\
    ',

    getTemplateData: function () {
      return {subtitle: this.options.displayOptions.updateManagerAssistantInfo ?
        StringUtil.localize('universal-directory.mappings.save.success.prompt.managerAssistant.subtitle') :
        StringUtil.localize('universal-directory.mappings.save.success.prompt.subtitle')
      };
    }
  });
});
