define([
  'underscore',
  'shared/views/components/BaseDropDown',
  './DropDownOptions'
],
  function (_, BaseDropDown, DropDownOptions) {

    return BaseDropDown.extend({
      initialize: function () {
        _(DropDownOptions.getData(this.state.get('tabId'),
          this.options.appModel.get('features'))).each(function (proto) {
            this.addOption(proto, this.options);
          }, this);
      }
    });
  });