var install     = require('./install'),
    shrinkwrap  = require('./shrinkwrap');

// Options
// - srcDir: root directory of install-with-shrinkwrap package
// - targetDir: root directory of package to run install-with-shrinkwrap on
// - targetPkg: package json for target package
// - packageToInstall: [@scope>]<name>[@<version range]
// - save: save to dependencies on new install
// - saveDev: save to devDependencies on new install
module.exports = function (options) {

  // 1. Run the install
  install(
    options.targetDir,
    options.targetPkg,
    options.packageToInstall,
    options.save,
    options.saveDev
  );

  // 2. Generate the shrinkwrap
  shrinkwrap(options.srcDir, options.targetDir);

};
